module.exports = function (x, indent) {
	const newLine = '\n';
	if (!indent) {
		x.replace(/^\s+/, (match) => {
			indent = match.replace(/\r|\n/g, '') || '\t';
		});
	}
	x = x.replace(/^\s*|\s*$/g, '').replace(/^|\r\n|\r|\n/g, (match) => {
		return match + indent;
	});
	x = newLine + x + newLine;
	return this.env.filters.safe(x);
};
