const assert = require('assert');
const blockIndent = require('..');
const context = {
	env: {
		filters: {
			safe: (x) => {
				return x;
			}
		}
	}
};
const N = '\n';
const T = '\t';
const gears = [
	'Acerola Rain Boots',
	'Bubble Rain Boots',
	'Custom Trail Boots',
	'Green Rain Boots',
	'Pro Trail Boots',
	'Trail Boots'
];

it('should indent lines', function () {
	assert.equal(blockIndent.call(context, N + gears.join(N) + N, T), N + gears.map((gear) => {
		return T + gear;
	}).join(N) + N);
});

it('should detect indent from source', function () {
	assert.equal(blockIndent.call(context, N + T + gears.join(N) + N), N + gears.map((gear) => {
		return T + gear;
	}).join(N) + N);
});
