var bMods = require('blogs_modules');
var args = bMods.args;
var Blog = require('./lib/-blog');

module.exports = function () {

	var blog = new Blog(process.cwd());

	if (args.build || args.gen || args.generate || args.compile) {
		blog.build();
	} else if (args.server) {
		require('./server');
	} else {
		require('./help');
	}

};
